// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract CoinFlip {
    address public owner;

    event BetPlaced(address indexed user, bool choice, uint256 amount);
    event Result(address indexed user, bool win, uint256 amount);

    constructor() {
        owner = msg.sender;
    }

    function flipCoin(bool _choice, address _tokenAddress, uint256 _amount) public {
        require(_amount > 0, "Amount must be greater than zero");
        IERC20 token = IERC20(_tokenAddress);
        require(token.transferFrom(msg.sender, address(this), _amount), "Transfer failed");

        bool result = (block.timestamp % 2 == 0);
        if (result == _choice) {
            require(token.transfer(msg.sender, _amount * 2), "Payout failed");
            emit Result(msg.sender, true, _amount * 2);
        } else {
            emit Result(msg.sender, false, 0);
        }

        emit BetPlaced(msg.sender, _choice, _amount);
    }

    function withdraw() public {
        require(msg.sender == owner, "Only the owner can withdraw");
        payable(owner).transfer(address(this).balance);
    }
}

